import React, { useState } from "react";
import axios from "axios";

const PostForm = () => {
  const initialState = {
    title: "",
    coverimage: null,
    images: [], // Changed to array to hold selected files
    description: "",
  };

  const [formData, setFormData] = useState(initialState);

  const handleChange = (e) => {
    const { name, files } = e.target;

    if (name === "images") {
      // Check if 'files' is an array before calling forEach on it
      const fileArray = Array.isArray(files) ? files : [files];

      setFormData({
        ...formData,
        [name]: fileArray,
      });
    } else {
      setFormData({
        ...formData,
        [name]: e.target.value,
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const postData = new FormData();
      postData.append("title", formData.title);
      postData.append("coverimage", formData.coverimage);
      formData.images.forEach((image) => {
        postData.append("images", image); // Append each image to FormData
      });
      postData.append("description", formData.description);

      const response = await axios.post("http://localhost:3000/upload", postData);

      if (response.status !== 201) {
        throw new Error("Failed to create featured news article");
      }

      // Reset form after successful submission
      setFormData(initialState);

      alert("Featured news article created successfully");
    } catch (error) {
      console.error("Error:", error.message);
      alert("Failed to create featured news article");
    }
  };

  return (
    <div className="max-w-md mx-auto mt-8">
      <form onSubmit={handleSubmit} className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
        <div className="mb-4">
          <label htmlFor="title" className="block text-gray-700 text-sm font-bold mb-2">Title:</label>
          <input
            type="text"
            id="title"
            name="title"
            value={formData.title}
            onChange={handleChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            required
          />
        </div>
        <div className="mb-4">
          <label htmlFor="coverimage" className="block text-gray-700 text-sm font-bold mb-2">Cover Image:</label>
          <input
            type="file"
            id="coverimage"
            name="coverimage"
            onChange={handleChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            required
          />
        </div>
        <div className="mb-4">
          <label htmlFor="images" className="block text-gray-700 text-sm font-bold mb-2">Images:</label>
          <input
            type="file"
            id="images"
            name="images"
            multiple
            onChange={handleChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            required
          />
        </div>
        <div className="mb-4">
          <label htmlFor="description" className="block text-gray-700 text-sm font-bold mb-2">Description:</label>
          <textarea
            id="description"
            name="description"
            value={formData.description}
            onChange={handleChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline h-32"
            required
          ></textarea>
        </div>
        <button type="submit" className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">Submit</button>
      </form>
    </div>
  );
};

export default PostForm;
