import React, { useState } from "react";
import axios from "axios";

const PostForm = () => {
  const initialState = {
    title: "",
    coverimage: "",
    images: [],
    description: "",
  };

  const [formData, setFormData] = useState(initialState);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      console.log("Form data before submission:", formData);

      const response = await axios.post(
        "http://localhost:3000/post",
        formData,
        {
          headers: {
            "Content-Type": "application/json",
          },
        }
      );

      console.log("Response after form submission:", response);

      if (response.status !== 201) {
        throw new Error("Failed to create featured news article");
      }

      // Reset form after successful submission
      setFormData(initialState);

      alert("Featured news article created successfully");
    } catch (error) {
      console.error("Error:", error.message);
      alert("Failed to create featured news article");
    }
  };

  return (
    <div className="max-w-md mx-auto mt-8">
      <form onSubmit={handleSubmit} className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
        <div className="mb-4">
          <label htmlFor="title" className="block text-gray-700 text-sm font-bold mb-2">Title:</label>
          <input
            type="text"
            id="title"
            name="title"
            value={formData.title}
            onChange={handleChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            required
          />
        </div>
        <div className="mb-4">
          <label htmlFor="coverimage" className="block text-gray-700 text-sm font-bold mb-2">Cover Image:</label>
          <input
            type="file"
            id="coverimage"
            name="coverimage"
            value={formData.coverimage}
            onChange={handleChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            required
          />
        </div>
        <div className="mb-4">
          <label htmlFor="images" className="block text-gray-700 text-sm font-bold mb-2">Images (comma-separated URLs):</label>
          <input
            type="text"
            id="images"
            name="images"
            value={formData.images.join(",")}
            onChange={(e) => setFormData({ ...formData, images: e.target.value.split(",") })}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            required
          />
        </div>
        <div className="mb-4">
          <label htmlFor="description" className="block text-gray-700 text-sm font-bold mb-2">Description:</label>
          <textarea
            id="description"
            name="description"
            value={formData.description}
            onChange={handleChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline h-32"
            required
          ></textarea>
        </div>
        <button type="submit" className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">Submit</button>
      </form>
    </div>
  );
};

export default PostForm;
