import React, { useState, useEffect } from "react";
import axios from "axios";

const PostForm = () => {

    const initialState = {
        title: "",
        coverimage: "",
        images: [],
        description: "",
      }

  const [formData, setFormData] = useState(initialState);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await axios.get(
          "http://localhost:3000/v1/api/featured/news"
        );
        console.log("Fetch data response:", response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    }
    fetchData();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      console.log("Form data before submission:", formData);

      const response = await axios.post(
        "http://localhost:3000/v1/api/featured/news",
        formData,
        {
          headers: {
            "Content-Type": "application/json",
          },
        }
      );

      console.log("Response after form submission:", response);

      if (response.status !== 201) {
        throw new Error("Failed to create featured news article");
      }

      // Reset form after successful submission
      setFormData({
        title: "",
        images: [],
        description: "",
      });

      alert("Featured news article created successfully");
    } catch (error) {
      console.error("Error:", error.message);
      alert("Failed to create featured news article");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="title">Title:</label>
        <input
          type="text"
          id="title"
          name="title"
          value={formData.title}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <label htmlFor="images">Images (comma-separated URLs):</label>
        <input
          type="text"
          id="images"
          name="images"
          value={formData.images.join(",")}
          onChange={(e) =>
            setFormData({ ...formData, images: e.target.value.split(",") })
          }
          required
        />
      </div>
      <div>
        <label htmlFor="description">Description:</label>
        <textarea
          id="description"
          name="description"
          value={formData.description}
          onChange={handleChange}
          required
        ></textarea>
      </div>
      <button type="submit">Submit</button>
    </form>
  );
};

export default PostForm;
